mapper.select:
SELECT COUNT(USER_ID)
	FROM USER
	WHERE USER_ID = #{userId}

SELECT COUNT(USER_NM)
	FROM USER
	WHERE USER_NM = #{userNm}

mapper.update:
UPDATE USER
SET USER_ROLE = 'Stopped'
WHERE USER_ID = #{userId }

UPDATE USER
USER_PWD = #{userPwd}, USER_NM = #{userNm}, USER_EMAIL = #{userEmail},
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_PWD
FROM USER
WHERE USER_ID = #{userId}
