mapper.update:
UPDATE BOARD
	SET BOARD_CNT = BOARD_CNT + 1
	WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
	SET BOARD_TITLE = #{boardTitle},
		BOARD_MAIN = #{boardMain},
		BOARD_STAR = #{boardStar},
		BOARD_CATE = #{boardCate}
	WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
	SET BOARD_TITLE = #{boardTitle},
		BOARD_MAIN = #{boardMain}
	WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT * FROM BOARD
WHERE 1 = 0 OR BOARD_TITLE LIKE CONCAT('%', #{boardSearch.searchKeyword}, '%') OR BOARD_MAIN LIKE CONCAT('%', #{boardSearch.searchKeyword}, '%') OR USER_ID LIKE CONCAT('%', #{boardSearch.searchKeyword}, '%')
AND BOARD_CATECD = #{boardSearch.boardCatecd}
WHERE 1 = 1
AND BOARD_TITLE LIKE CONCAT('%', #{boardSearch.searchKeyword}, '%')
AND BOARD_MAIN LIKE CONCAT('%', #{boardSearch.searchKeyword}, '%')
AND USER_ID LIKE CONTCAT('%', #{boardSearch.searchKeyword}, '%')
AND BOARD_CATECD = #{boardSearch.boardCatecd}
WHERE 1 = 1
AND BOARD_CATECD = #{boardSearch.boardCatecd}
LIMIT #{cri.amount} OFFSET #{cri.startNum}

mapper.update:
UPDATE FILEUPLOAD
	SET FILE_NAME = #{fileName},
		ORI_FILE_NAME = #{oriFileName},
		FILE_PATH = #{filePath},
		FILE_CATE = #{fileCate}
	WHERE BOARD_NO = #{boardNo}
	  AND FILE_NO = #{fileNo}

mapper.delete:
DELETE FROM FILEUPLOAD
	WHERE BOARD_NO = #{boardNo}
	  AND FILE_NO = #{fileNo}

